mapper.select:
select * from
    (select * from doctor do

         and id = #{data.id} 
         and user_id = #{data.userId} 
         and department_id = #{data.departmentId}

        and status & #{data.status} != 0
        and am_std > #{data.amStd}
        and pm_std > #{data.pmStd}



            order by id asc


            order by seniority asc
            order by seniority desc


) as d
inner join
    (select id as user_id, name, gender, phone, email from user

        and name like concat ('%', #{data.name}, '%')
        and phone like concat ('%', #{data.phone}, '%')
        and email like concat ('%', #{data.email}, '%')
        and gender & #{data.gender} != 0
        and role & #{data.role} != 0

) as u
on u.user_id = d.user_id
LIMIT #{size} OFFSET #{offset}

mapper.update:
update doctor
id = #{id}, user_id = #{userId}, department_id = #{departmentId}, seniority = #{seniority}, status = #{status}, position = #{position} am_std = #{amStd}, pm_std = #{pmStd},
where id = #{id}

mapper.select:
select count(1) from
(select user_id from doctor

     and id = #{id} 
     and user_id = #{userId} 
     and department_id = #{departmentId}

    and status & #{status} != 0
    and am_std > #{amStd}
    and pm_std > #{pmStd}
) as d
inner join
(select id from user

    and name like concat ('%', #{name}, '%')
    and phone like concat ('%', #{phone}, '%')
    and email like concat ('%', #{email}, '%')
    and gender & #{gender} != 0
    and role & #{role} != 0

) as u
on u.id = d.user_id

select * FROM (
select d.*, u.name, u.gender, COALESCE(r.stock, 0) as stock from
    (select * from doctor

         and department_id = #{data.departmentId}
        and status & #{data.status} != 0

    ) as d inner join
    (select id as user_id, name, gender from user

        and gender & #{data.gender} != 0
        and role & #{data.role} != 0

    ) as u on u.user_id = d.user_id left join
    (select doctor_id, COALESCE(sum(1), 0) as stock from register
        where status != 8 and section = #{data.section} and date between #{data.beginDate} and #{data.endDate}
        group by doctor_id
    ) as r on r.doctor_id = d.id
) as tmp



            stock < am_std


            stock < pm_std


    and name like concat ('%', #{data.name}, '%') OR description LIKE CONCAT('%', #{data.name}, '%')

LIMIT #{size} OFFSET #{offset}
